import React from 'react';
import { Route } from 'react-router-dom';
import { createStructuredSelector } from 'reselect'
import { connect } from 'react-redux';

import { fecthCollectionsStartAsync } from '../../redux/shop/shop.actions';
import {  selectCollectionLoading } from '../../redux/shop/shop.selectors';

import WithSpinner from '../../components/with-spiner/with-spiner.component';

import CollectionsOverviewContainer from '../../components/collections-overview/collections-overview.container';
import CollectionPageCotainer from '../../page/collection/collection.component';


const CollectionPageWithSpinner = WithSpinner(CollectionPage);

class ShopPage extends React.Component {
    componentDidMount() {
        const  {fecthCollectionsStartAsync} = this.props;
        fecthCollectionsStartAsync();
    }

    render(){
        const { match, isCollectionLoading } = this.props
        return(
            <div className='shop-page'>
                <Route 
                    exact path={`${match.path}`} 
                    component={CollectionsOverviewContainer} 
                />
                <Route 
                    path={`${match.path}/:collectionId`} 
                    component={CollectionPageCotainer} 
                />    
            </div>
        )
    }
}


const mapDispatchToProps = dispatch => ({
    fecthCollectionsStartAsync: () => dispatch(fecthCollectionsStartAsync())
})

export default connect(null, mapDispatchToProps)(ShopPage);
