import React from 'react';
import { Route } from 'react-router-dom';
import { createStructuredSelector } from 'reselect'
import { connect } from 'react-redux';

import { fecthCollectionsStart } from '../../redux/shop/shop.actions';
import { selectCollectionFetching } from '../../redux/shop/shop.selectors';

import WithSpinner from '../../components/with-spiner/with-spiner.component';

import CollectionsOverview from '../../components/collections-overview/collections-overview.component';
import CollectionPage from '../../page/collection/collection.component';


const CollectionsOverviewWithSpinner = WithSpinner(CollectionsOverview);
const CollectionPageWithSpinner = WithSpinner(CollectionPage);

class ShopPage extends React.Component {
    componentDidMount() {
        const  {fecthCollectionsStart} = this.props;
        fecthCollectionsStart();
    }

    render(){
        const { match, isCollectionFetching } = this.props
        return(
            <div className='shop-page'>
                <Route exact path={`${match.path}`} render={(props) => <CollectionsOverviewWithSpinner isLoading={isCollectionFetching} {...props} /> } />
                <Route path={`${match.path}/:collectionId`} render={(props) => <CollectionPageWithSpinner isLoading={isCollectionFetching} {...props} /> } />    
            </div>
        )
    }
}

const mapStateToProps = createStructuredSelector({
    isCollectionFetching: selectCollectionFetching
})

const mapDispatchToProps = dispatch => ({
    fecthCollectionsStartAsync: () => dispatch(fecthCollectionsStartAsync)
})

export default connect(mapStateToProps, mapDispatchToProps)(ShopPage);
